import { firstValueFrom } from "rxjs";
import * as i0 from "@angular/core";
import * as i1 from "@angular/router";
import * as i2 from "@angular/common/http";
import * as i3 from "../router-params.service";
var ActionType;
(function (ActionType) {
    ActionType[ActionType["AuthAccount"] = 1] = "AuthAccount";
    ActionType[ActionType["AuthPin"] = 2] = "AuthPin";
    ActionType[ActionType["SelectActions"] = 3] = "SelectActions";
    ActionType[ActionType["Balance"] = 4] = "Balance";
    ActionType[ActionType["MenuOrExit"] = 5] = "MenuOrExit";
    ActionType[ActionType["Withdraw"] = 6] = "Withdraw";
    ActionType[ActionType["Blocked"] = 7] = "Blocked";
})(ActionType || (ActionType = {}));
export class AtmComponent {
    constructor(route, http, router, params, baseUrl) {
        this.route = route;
        this.http = http;
        this.router = router;
        this.params = params;
        this.baseUrl = baseUrl;
        this.currActionType = ActionType.AuthAccount;
        this.balance = 0;
        this.accountNumber = 0;
        this.creditId = 0;
        this.pin = '1111';
        this.invalidAttemptsCount = 0;
    }
    ngOnInit() {
    }
    async pinEntered() {
        try {
            this.creditId = await firstValueFrom(this.http.post(this.baseUrl + 'atm/pin', {
                accountNumber: this.accountNumber,
                pin: this.pin
            }));
        }
        catch (e) {
            this.invalidAttemptsCount++;
            if (this.invalidAttemptsCount >= 3) {
                this.currActionType = ActionType.Blocked;
            }
            alert('Неверный номер карты или пин');
        }
        this.invalidAttemptsCount = 0;
        this.currActionType = ActionType.SelectActions;
    }
    accountEntered() {
        this.currActionType = ActionType.AuthPin;
    }
    menuSelected() {
        this.currActionType = ActionType.SelectActions;
    }
    exitSelected() {
        this.currActionType = ActionType.AuthAccount;
    }
    async balanceSelected() {
        this.balance = await firstValueFrom(this.http.get(this.baseUrl + 'atm/' + this.creditId));
        this.currActionType = ActionType.Balance;
        if (prompt('Печатать чек?')) {
            alert(`Ваш баланс - ${this.balance}`);
        }
    }
    async withdrawSelected() {
        this.balance = await firstValueFrom(this.http.get(this.baseUrl + 'atm'));
        this.currActionType = ActionType.Balance;
        if (prompt('Печатать чек?')) {
            alert(`Ваш баланс - ${this.balance}`);
        }
    }
}
