import { firstValueFrom } from "rxjs";
import * as moment from "moment/moment";
import * as i0 from "@angular/core";
import * as i1 from "@angular/router";
import * as i2 from "@angular/common/http";
import * as i3 from "../../../../router-params.service";
export class CreditComponent {
    constructor(route, http, router, params, baseUrl) {
        this.route = route;
        this.http = http;
        this.router = router;
        this.params = params;
        this.baseUrl = baseUrl;
        this.isCreateMode = false;
        this.timeSpan = 3;
    }
    async ngOnInit() {
        this.isCreateMode = !this.params.depositId;
        this.lookUps = await firstValueFrom(this.http.get(this.baseUrl + 'lookups/deposit'));
        this.refreshDeposit();
    }
    async refreshDeposit() {
        const { clientId, depositId } = this.params;
        if (!this.isCreateMode) {
            this.deposit = await firstValueFrom(this.http.get(this.baseUrl + 'clients/' + clientId + '/deposit/' + depositId));
            console.log(this.deposit);
        }
        else {
            this.deposit = {
                contractNumber: "",
                currencyType: {
                    id: 1,
                    name: ""
                },
                amount: 0,
                startDate: new Date(),
                depositType: undefined,
                endDate: moment().add(this.timeSpan, 'months').toDate(),
                nextInterestPayDate: undefined,
                id: 0,
                isActive: true,
                interestAccount: undefined,
                depositAccount: undefined
            };
        }
    }
    async save() {
        const id = await firstValueFrom(this.http.post(this.baseUrl + 'clients/' + this.params.clientId + '/deposit', this.deposit))
            .catch(p => alert('Вы попытались сломать систему. Соответствующая информация будет отправлена вашему непосредственному начальнику'));
        await this.router.navigateByUrl(`/clients/` + this.params.clientId + '/deposits/' + id);
        alert('Депозит успешно сохранён');
        await this.ngOnInit();
    }
    updateDates() {
        // @ts-ignore
        this.deposit.endDate = moment().add(this.timeSpan, 'months').toDate();
    }
    async payInterest() {
        await firstValueFrom(this.http.post(this.baseUrl + 'clients/' + this.params.clientId + '/deposit/' + this.params.depositId + '/pay-interest', null));
        alert('Готово!');
    }
    async close() {
        await firstValueFrom(this.http.post(this.baseUrl + 'clients/' + this.params.clientId + '/deposit/' + this.params.depositId + '/close', null));
        alert('Депозит закрыт.');
        await this.ngOnInit();
    }
}
