import { __decorate, __param } from "tslib";
import { Component, Inject } from '@angular/core';
import { firstValueFrom } from 'rxjs';
let ClientComponent = class ClientComponent {
    constructor(route, http, router, params, baseUrl) {
        this.route = route;
        this.http = http;
        this.router = router;
        this.params = params;
        this.baseUrl = baseUrl;
        this.isCreateMode = false;
    }
    async ngOnInit() {
        this.isCreateMode = !this.params.clientId;
        this.lookUps = await firstValueFrom(this.http.get(this.baseUrl + 'lookups'));
        this.refreshClient();
    }
    async refreshClient() {
        const clientId = this.params.clientId;
        if (!this.isCreateMode) {
            this.client = await firstValueFrom(this.http.get(this.baseUrl + 'clients/' + clientId));
        }
    }
    async deleteClient() {
        await firstValueFrom(this.http.post(this.baseUrl + 'clients/' + this.params.clientId + '/delete', null));
        await this.router.navigateByUrl(`/clients`);
    }
};
